(("undefined"!=typeof globalThis?globalThis:self).makoChunk_antd=("undefined"!=typeof globalThis?globalThis:self).makoChunk_antd||[]).push([["39a1999c"],{"39a1999c":function(e,a,n){"use strict";n.d(a,"__esModule",{value:!0}),n.d(a,"texts",{enumerable:!0,get:function(){return t;}}),n("c716787e");let t=[{value:"Ant Design supports the ",paraId:0,tocIndex:0},{value:"last 2 versions of modern browsers",paraId:0,tocIndex:0},{value:". If you need to be compatible with legacy browsers, please perform downgrade processing according to actual needs:",paraId:0,tocIndex:0},{value:"Feature",paraId:1,tocIndex:0},{value:"antd version",paraId:1,tocIndex:0},{value:"Compatibility",paraId:1,tocIndex:0},{value:"Minimum Chrome Version",paraId:1,tocIndex:0},{value:"Compatibility workaround",paraId:1,tocIndex:0},{value:":where Selector",paraId:1,tocIndex:0},{value:">=5.0.0",paraId:1,tocIndex:0},{value:"caniuse",paraId:1,tocIndex:0},{value:"Chrome 88",paraId:1,tocIndex:0},{value:'<StyleProvider hashPriority="high">',paraId:1,tocIndex:0},{value:"CSS Logical Properties",paraId:1,tocIndex:0},{value:">=5.0.0",paraId:1,tocIndex:0},{value:"caniuse",paraId:1,tocIndex:0},{value:"Chrome 89",paraId:1,tocIndex:0},{value:"<StyleProvider transformers={[legacyLogicalPropertiesTransformer]}>",paraId:1,tocIndex:0},{value:"If you need to support older browsers, please use ",paraId:2,tocIndex:0},{value:"@ant-design/cssinjs",paraId:2,tocIndex:0},{value:" ",paraId:2,tocIndex:0},{value:"StyleProvider",paraId:2,tocIndex:0},{value:" for degradation handling according to your actual requirements.",paraId:2,tocIndex:0},{value:":where",paraId:3},{value:"antd version: ",paraId:4,tocIndex:1},{value:">=5.0.0",paraId:4,tocIndex:1},{value:"MDN: ",paraId:4,tocIndex:1},{value:":where",paraId:4,tocIndex:1},{value:"Browser Compatibility: ",paraId:4,tocIndex:1},{value:"caniuse",paraId:4,tocIndex:1},{value:"Minimum Chrome Version Supported: 88",paraId:4,tocIndex:1},{value:"Default Enabled: Yes",paraId:4,tocIndex:1},{value:'The CSS-in-JS feature of Ant Design uses the ":where" selector by default to lower the CSS selector specificity, reducing the additional cost of adjusting custom styles when upgrading for users. However, the compatibility of the ":where" syntax is relatively poor in older browsers (',paraId:5,tocIndex:1},{value:"compatibility",paraId:5,tocIndex:1},{value:"). In certain scenarios, if you need to support older browsers, you can use ",paraId:5,tocIndex:1},{value:"@ant-design/cssinjs",paraId:5,tocIndex:1},{value:" to disable the default lowering of specificity (please ensure version consistency with antd).",paraId:5,tocIndex:1},{value:"import { StyleProvider } from '@ant-design/cssinjs';\n\n// Config `hashPriority` to `high` instead of default `low`\n// Which will remove `:where` wrapper\nexport default () => (\n  <StyleProvider hashPriority=\"high\">\n    <MyApp />\n  </StyleProvider>\n);\n",paraId:6,tocIndex:1},{value:"It will turn ",paraId:7,tocIndex:1},{value:":where",paraId:7,tocIndex:1},{value:" to class selector:",paraId:7,tocIndex:1},{value:"--  :where(.css-bAMboO).ant-btn {\n++  .css-bAMboO.ant-btn {\n      color: #fff;\n    }\n",paraId:8,tocIndex:1},{value:"Note: After turning off the ",paraId:9,tocIndex:1},{value:":where",paraId:9,tocIndex:1},{value:" downgrade, you may need to manually adjust the priority of some styles. Or you can ",paraId:9,tocIndex:1},{value:"use PostCSS plugin",paraId:9,tocIndex:1},{value:" to raise application css selector priority. PostCSS provides many plugins can help on this. e.g:",paraId:9,tocIndex:1},{value:"postcss-scopify",paraId:10,tocIndex:1},{value:"postcss-increase-specificity",paraId:10,tocIndex:1},{value:"postcss-add-root-selector",paraId:10,tocIndex:1},{value:"Raise priority through plugin:",paraId:11,tocIndex:1},{value:"--  .my-btn {\n++  #root .my-btn {\n      background: red;\n    }\n",paraId:12,tocIndex:1},{value:"antd version: ",paraId:13,tocIndex:2},{value:">=5.0.0",paraId:13,tocIndex:2},{value:"MDN\uFF1A",paraId:13,tocIndex:2},{value:"CSS Logical Properties",paraId:13,tocIndex:2},{value:"Browser Compatibility: ",paraId:13,tocIndex:2},{value:"caniuse",paraId:13,tocIndex:2},{value:"Minimum Chrome Version Supported: 89",paraId:13,tocIndex:2},{value:"Default Enabled: Yes",paraId:13,tocIndex:2},{value:"To unify LTR and RTL styles, Ant Design uses CSS logical properties. For example, the original ",paraId:14,tocIndex:2},{value:"margin-left",paraId:14,tocIndex:2},{value:" is replaced by ",paraId:14,tocIndex:2},{value:"margin-inline-start",paraId:14,tocIndex:2},{value:", so that it is the starting position spacing under both LTR and RTL. If you need to be compatible with older browsers, you can configure ",paraId:14,tocIndex:2},{value:"transformers",paraId:14,tocIndex:2},{value:" through the ",paraId:14,tocIndex:2},{value:"StyleProvider",paraId:14,tocIndex:2},{value:" of ",paraId:14,tocIndex:2},{value:"@ant-design/cssinjs",paraId:14,tocIndex:2},{value:":",paraId:14,tocIndex:2},{value:"import { legacyLogicalPropertiesTransformer, StyleProvider } from '@ant-design/cssinjs';\n\n// `transformers` provides a way to transform CSS properties\nexport default () => (\n  <StyleProvider transformers={[legacyLogicalPropertiesTransformer]}>\n    <MyApp />\n  </StyleProvider>\n);\n",paraId:15,tocIndex:2},{value:"When toggled, styles will downgrade CSS logical properties:",paraId:16,tocIndex:2},{value:".ant-modal-root {\n-- inset: 0;\n++ top: 0;\n++ right: 0;\n++ bottom: 0;\n++ left: 0;\n}\n",paraId:17,tocIndex:2},{value:"@layer",paraId:3},{value:"antd version: ",paraId:18,tocIndex:3},{value:">=5.17.0",paraId:18,tocIndex:3},{value:"MDN\uFF1A",paraId:18,tocIndex:3},{value:"CSS @layer",paraId:18,tocIndex:3},{value:"Browser Compatibility: ",paraId:18,tocIndex:3},{value:"caniuse",paraId:18,tocIndex:3},{value:"Minimum Chrome Version Supported: 99",paraId:18,tocIndex:3},{value:"Default Enabled: No",paraId:18,tocIndex:3},{value:"Ant Design supports configuring ",paraId:19,tocIndex:3},{value:"@layer",paraId:19,tocIndex:3},{value:" for unified css priority downgrade since ",paraId:19,tocIndex:3},{value:"5.17.0",paraId:19,tocIndex:3},{value:". After the downgrade, the style of antd will always be lower than the default CSS selector priority, so that users can override the style (please be sure to check the browser compatibility of ",paraId:19,tocIndex:3},{value:"@layer",paraId:19,tocIndex:3},{value:").When enable ",paraId:19,tocIndex:3},{value:"layer",paraId:19,tocIndex:3},{value:", the child element ",paraId:19,tocIndex:3},{value:"must",paraId:19,tocIndex:3},{value:" wrap ",paraId:19,tocIndex:3},{value:"ConfigProvider",paraId:19,tocIndex:3},{value:" to update the icon-related styles:",paraId:19,tocIndex:3},{value:"import { StyleProvider } from '@ant-design/cssinjs';\nimport { ConfigProvider } from 'antd';\n\nexport default () => (\n  <StyleProvider layer>\n    <ConfigProvider>\n      <MyApp />\n    </ConfigProvider>\n  </StyleProvider>\n);\n",paraId:20,tocIndex:3},{value:"antd styles will be encapsulated in ",paraId:21,tocIndex:3},{value:"@layer",paraId:21,tocIndex:3},{value:" to lower the priority:",paraId:21,tocIndex:3},{value:"++  @layer antd {\n      :where(.css-bAMboO).ant-btn {\n        color: #fff;\n      }\n++  }\n",paraId:22,tocIndex:3},{value:"\u26A0\uFE0F zeroRuntime Scenario Notes (Added in 6.0.0)",paraId:23,tocIndex:3},{value:"When ",paraId:24,tocIndex:3},{value:"zeroRuntime",paraId:24,tocIndex:3},{value:" is enabled, Ant Design\u2019s styles are precompiled into a standalone ",paraId:24,tocIndex:3},{value:"antd.css",paraId:24,tocIndex:3},{value:" file. If you also enable the ",paraId:24,tocIndex:3},{value:"@layer",paraId:24,tocIndex:3},{value:" specificity\u2013lowering mechanism, you must ensure that ",paraId:24,tocIndex:3},{value:"antd.css",paraId:24,tocIndex:3},{value:" is placed inside the same layer (e.g., ",paraId:24,tocIndex:3},{value:"layer(antd)",paraId:24,tocIndex:3},{value:"). Otherwise, its specificity will be higher than the styles injected by StyleProvider, causing the lowering mechanism to fail or resulting in unexpected override behavior.",paraId:24,tocIndex:3},{value:"/* global.css / app.css */\n@layer theme, base, antd, components, utilities;\n\n/* The precompiled antd.css output by zeroRuntime must explicitly specify a layer */\n@import url(antd.css) layer(antd);\n",paraId:25,tocIndex:3},{value:"If you cannot use the ",paraId:26,tocIndex:3},{value:"@import ... layer()",paraId:26,tocIndex:3},{value:" syntax, you may wrap the content during your build process instead:",paraId:26,tocIndex:3},{value:"@layer antd {\n  /* contents of antd.css */\n}\n",paraId:27,tocIndex:3},{value:"antd version: ",paraId:28,tocIndex:4},{value:">=6.0.0",paraId:28,tocIndex:4},{value:"Browser Compatibility: Automatically adds browser prefixes for wider browser support",paraId:28,tocIndex:4},{value:"Default Enabled: No",paraId:28,tocIndex:4},{value:"Some styles rely on browser prefixes for compatibility. The ",paraId:29,tocIndex:4},{value:"autoPrefixer",paraId:29,tocIndex:4},{value:" transformer can automatically add browser prefixes to styles, ensuring they work properly across different browsers.",paraId:29,tocIndex:4},{value:"import { autoPrefixTransformer, StyleProvider } from '@ant-design/cssinjs';\n\nexport default () => (\n  <StyleProvider transformers={[autoPrefixTransformer]}>\n    <MyApp />\n  </StyleProvider>\n);\n",paraId:30,tocIndex:4},{value:"The final transformed styles:",paraId:31,tocIndex:4},{value:"  .sample-box {\n--  user-select: none;\n++  -webkit-user-select: none;\n++  -moz-user-select: none;\n++  -ms-user-select: none;\n++  user-select: none;\n  }\n",paraId:32,tocIndex:4},{value:"In responsive web development, there is a need for a convenient and flexible way to achieve page adaptation and responsive design. The ",paraId:33,tocIndex:5},{value:"px2remTransformer",paraId:33,tocIndex:5},{value:" transformer can quickly and accurately convert pixel units in style sheets to rem units relative to the root element (HTML tag), enabling the implementation of adaptive and responsive layouts.",paraId:33,tocIndex:5},{value:"import { px2remTransformer, StyleProvider } from '@ant-design/cssinjs';\n\nconst px2rem = px2remTransformer({\n  rootValue: 32, // 32px = 1rem; @default 16\n});\n\nexport default () => (\n  <StyleProvider transformers={[px2rem]}>\n    <MyApp />\n  </StyleProvider>\n);\n",paraId:34,tocIndex:5},{value:"The resulting transformed styles:",paraId:35,tocIndex:5},{value:" .px2rem-box {\n-  width: 400px;\n+  width: 12.5rem;\n   background-color: green;\n-  font-size: 32px;\n+  font-size: 1rem;\n   border: 10PX solid #f0f;\n }\n\n @media only screen and (max-width: 600px) {\n   .px2rem-box {\n     background-color: red;\n-    margin: 10px;\n+    margin: 0.3125rem;\n   }\n }\n",paraId:36,tocIndex:5},{value:"Parameter",paraId:37,tocIndex:6},{value:"Description",paraId:37,tocIndex:6},{value:"Type",paraId:37,tocIndex:6},{value:"Default",paraId:37,tocIndex:6},{value:"rootValue",paraId:37,tocIndex:6},{value:"Font size of the root element",paraId:37,tocIndex:6},{value:"number",paraId:37,tocIndex:6},{value:"16",paraId:37,tocIndex:6},{value:"precision",paraId:37,tocIndex:6},{value:"Decimal places for the converted value",paraId:37,tocIndex:6},{value:"number",paraId:37,tocIndex:6},{value:"5",paraId:37,tocIndex:6},{value:"mediaQuery",paraId:37,tocIndex:6},{value:"Whether to convert px in media queries",paraId:37,tocIndex:6},{value:"boolean",paraId:37,tocIndex:6},{value:"false",paraId:37,tocIndex:6},{value:"For more details, please refer to: ",paraId:38,tocIndex:6},{value:"px2rem.ts#Options",paraId:38,tocIndex:6},{value:"Since ",paraId:39,tocIndex:7},{value:"<style />",paraId:39,tocIndex:7},{value:" tag insertion is different from normal DOM in Shadow DOM scenario, you need to use ",paraId:39,tocIndex:7},{value:"StyleProvider",paraId:39,tocIndex:7},{value:" of ",paraId:39,tocIndex:7},{value:"@ant-design/cssinjs",paraId:39,tocIndex:7},{value:" to configure the ",paraId:39,tocIndex:7},{value:"container",paraId:39,tocIndex:7},{value:" property to set the insertion position:",paraId:39,tocIndex:7},{value:"import { StyleProvider } from '@ant-design/cssinjs';\nimport { createRoot } from 'react-dom/client';\n\nconst shadowRoot = someEle.attachShadow({ mode: 'open' });\nconst container = document.createElement('div');\nshadowRoot.appendChild(container);\nconst root = createRoot(container);\n\nroot.render(\n  <StyleProvider container={shadowRoot}>\n    <MyApp />\n  </StyleProvider>,\n);\n",paraId:40,tocIndex:7},{value:"In some cases, you may need antd to coexist with other style libraries, such as ",paraId:41,tocIndex:8},{value:"Tailwind CSS",paraId:41,tocIndex:8},{value:", ",paraId:41,tocIndex:8},{value:"Emotion",paraId:41,tocIndex:8},{value:", ",paraId:41,tocIndex:8},{value:"styled-components",paraId:41,tocIndex:8},{value:", etc. Unlike traditional CSS solutions, these third-party libraries are often not easy to override antd styles by increasing CSS selector priority. You can configure ",paraId:41,tocIndex:8},{value:"@layer",paraId:41,tocIndex:8},{value:" for antd to lower its CSS selector weight, and arrange ",paraId:41,tocIndex:8},{value:"@layer",paraId:41,tocIndex:8},{value:" order to solve style override problems:",paraId:41,tocIndex:8},{value:"@layer",paraId:3},{value:"As mentioned earlier, when using StyleProvider, you must wrap ConfigProvider to update icon-related styles:",paraId:42,tocIndex:9},{value:"import { StyleProvider } from '@ant-design/cssinjs';\n\nexport default () => (\n  <StyleProvider layer>\n    <ConfigProvider>\n      <MyApp />\n    </ConfigProvider>\n  </StyleProvider>\n);\n",paraId:43,tocIndex:9},{value:"@layer",paraId:3},{value:"Before starting the following configuration, you need to enable ",paraId:44,tocIndex:10},{value:"@layer",paraId:45,tocIndex:10},{value:" feature.",paraId:44,tocIndex:10},{value:"In global.css, adjust ",paraId:46,tocIndex:11},{value:"@layer",paraId:46,tocIndex:11},{value:" to control the order of style override. Place ",paraId:46,tocIndex:11},{value:"tailwind-base",paraId:46,tocIndex:11},{value:" before ",paraId:46,tocIndex:11},{value:"antd",paraId:46,tocIndex:11},{value:":",paraId:46,tocIndex:11},{value:"@layer tailwind-base, antd;\n\n@layer tailwind-base {\n  @tailwind base;\n}\n@tailwind components;\n@tailwind utilities;\n",paraId:47,tocIndex:11},{value:"In global.css, adjust ",paraId:48,tocIndex:12},{value:"@layer",paraId:48,tocIndex:12},{value:" to control the order of style override. Place ",paraId:48,tocIndex:12},{value:"antd",paraId:48,tocIndex:12},{value:" in the right position:",paraId:48,tocIndex:12},{value:"@layer theme, base, antd, components, utilities;\n\n@import 'tailwindcss';\n",paraId:49,tocIndex:12},{value:"If you are using Ant Design\u2019s ",paraId:50,tocIndex:13},{value:"reset.css",paraId:50,tocIndex:13},{value:", you need to assign it to a specific ",paraId:50,tocIndex:13},{value:"@layer",paraId:50,tocIndex:13},{value:" to prevent it from overriding the lowered-specificity antd styles. Similarly, in the ",paraId:50,tocIndex:13},{value:"zeroRuntime",paraId:50,tocIndex:13},{value:" scenario, if you import ",paraId:50,tocIndex:13},{value:"antd.css",paraId:50,tocIndex:13},{value:" separately, you must also place it inside ",paraId:50,tocIndex:13},{value:"layer(antd)",paraId:50,tocIndex:13},{value:" to keep the layer hierarchy consistent:",paraId:50,tocIndex:13},{value:"/* Both reset.css and antd.css must specify a layer */\n@layer reset, antd;\n\n/* reset styles */\n@import url(reset.css) layer(reset);\n\n/* antd styles */\n@import url(antd.css) layer(antd);\n",paraId:51,tocIndex:13},{value:"This ensures that:",paraId:52,tocIndex:13},{value:"reset.css will not override Ant Design styles that have been lowered via @layer",paraId:53,tocIndex:13},{value:"antd.css (in zeroRuntime mode) stays aligned with the layer injected by StyleProvider",paraId:53,tocIndex:13},{value:"Layer order still works correctly with third-party styling systems such as Tailwind, Emotion, or other CSS-in-JS libraries",paraId:53,tocIndex:13},{value:"After configuring ",paraId:54,tocIndex:14},{value:"@layer",paraId:54,tocIndex:14},{value:" for antd, you don't need to do any additional configuration for other CSS-in-JS libraries. Your CSS-in-JS can completely override antd styles.",paraId:54,tocIndex:14},{value:"When using SSR, styles are often rendered inline in HTML through ",paraId:55,tocIndex:15},{value:"<style />",paraId:55,tocIndex:15},{value:". At this time, please make sure that the styles with the specified ",paraId:55,tocIndex:15},{value:"@layer",paraId:55,tocIndex:15},{value:" priority order are loaded before ",paraId:55,tocIndex:15},{value:"@layer",paraId:55,tocIndex:15},{value:" is used.",paraId:55,tocIndex:15},{value:'<head>\n  <!-- SSR Injection style -->\n  <style>\n    @layer antd {\n      /** ... */\n    }\n  </style>\n\n  <!-- css file contains @layer xxx, antd; -->\n  <link rel="stylesheet" href="/b9a0m0b9o0o3.css" />\n  <!-- or write @layer xxx, antd; in html directly -->\n  <style>\n    @layer xxx, antd;\n  </style>\n</head>\n',paraId:56,tocIndex:16},{value:'<head>\n  <!-- css file contains @layer xxx, antd; -->\n  <link rel="stylesheet" href="/b9a0m0b9o0o3.css" />\n  <!-- or write @layer xxx, antd; in html directly -->\n  <style>\n    @layer xxx, antd;\n  </style>\n\n  <!-- SSR Injection style -->\n  <style>\n    @layer antd {\n      /** ... */\n    }\n  </style>\n</head>\n',paraId:57,tocIndex:17}];}}]);