(("undefined"!=typeof globalThis?globalThis:self).makoChunk_antd=("undefined"!=typeof globalThis?globalThis:self).makoChunk_antd||[]).push([["ffc970b7"],{ffc970b7:function(e,a,n){"use strict";n.d(a,"__esModule",{value:!0}),n.d(a,"texts",{enumerable:!0,get:function(){return t;}}),n("55081ba7");let t=[{value:"This guide explains how to enable AI tools like Cursor, Windsurf, and Claude to better understand Ant Design.",paraId:0},{value:"We support ",paraId:1,tocIndex:0},{value:"LLMs.txt",paraId:1,tocIndex:0},{value:" files for making the Ant Design documentation available to large language models (LLMs). This feature helps AI tools better understand our component library, its APIs, and usage patterns.",paraId:1,tocIndex:0},{value:"We provide several LLMs.txt routes to help AI tools access our documentation:",paraId:2,tocIndex:1},{value:"llms.txt",paraId:3,tocIndex:1},{value:" - Contains a structured overview of all components and their documentation links",paraId:3,tocIndex:1},{value:"llms-full.txt",paraId:3,tocIndex:1},{value:" - Provides comprehensive documentation including implementation details and examples",paraId:3,tocIndex:1},{value:"Use the ",paraId:4,tocIndex:3},{value:"@Docs",paraId:4,tocIndex:3},{value:" feature in Cursor to include the LLMs.txt files in your project. This helps Cursor provide more accurate code suggestions and documentation for Ant Design components.",paraId:4,tocIndex:3},{value:"Read more about @Docs in Cursor",paraId:5,tocIndex:3},{value:"Reference the LLMs.txt files using ",paraId:6,tocIndex:4},{value:"@",paraId:6,tocIndex:4},{value:" or in your ",paraId:6,tocIndex:4},{value:".windsurf/rules",paraId:6,tocIndex:4},{value:" files to enhance Windsurf's understanding of Ant Design components.",paraId:6,tocIndex:4},{value:"Read more about Windsurf Memories",paraId:7,tocIndex:4},{value:"In Claude Code, add ",paraId:8,tocIndex:5},{value:"LLMs.txt",paraId:8,tocIndex:5},{value:" to the workspace Knowledge Base (Docs / Context Files) configuration. This allows the file to be referenced during code completion and explanation, improving understanding of Ant Design components.",paraId:8,tocIndex:5},{value:"Learn more about Claude Code document context configuration",paraId:9,tocIndex:5},{value:"In Gemini CLI, you can specify the ",paraId:10,tocIndex:6},{value:"LLMs.txt",paraId:10,tocIndex:6},{value:" file path with the ",paraId:10,tocIndex:6},{value:"--context",paraId:10,tocIndex:6},{value:" parameter or in ",paraId:10,tocIndex:6},{value:".gemini/config.json",paraId:10,tocIndex:6},{value:", enabling Gemini to reference the document when answering or generating code.",paraId:10,tocIndex:6},{value:"Learn more about Gemini CLI context configuration",paraId:11,tocIndex:6},{value:"In Trae, place the ",paraId:12,tocIndex:7},{value:"LLMs.txt",paraId:12,tocIndex:7},{value:" file into the project\u2019s knowledge sources and enable referencing in the settings. This allows Trae to better support Ant Design components when generating or analyzing code.",paraId:12,tocIndex:7},{value:"Learn more about Trae knowledge sources",paraId:13,tocIndex:7},{value:"In Qoder, you can add ",paraId:14,tocIndex:8},{value:"LLMs.txt",paraId:14,tocIndex:8},{value:" as an external knowledge file in ",paraId:14,tocIndex:8},{value:".qoder/config.yml",paraId:14,tocIndex:8},{value:", or temporarily reference it in a conversation with ",paraId:14,tocIndex:8},{value:"@docs LLMs.txt",paraId:14,tocIndex:8},{value:", enhancing support for Ant Design components.",paraId:14,tocIndex:8},{value:"Learn more about Qoder configuration",paraId:15,tocIndex:8},{value:"Any AI tool that supports LLMs.txt can use these routes to better understand Ant Design. Simply point your tool to any of the routes above.",paraId:16,tocIndex:9}];}}]);